module.factory("request",function($http,$q){return function(option){return option.url=option.url+"?timeStamp="+(new Date).valueOf(),$http(option).then(function(response){var defer=$q.defer();return angular.isUndefined(response)?defer.reject({type:-1,data:response}):defer.resolve(response.data),defer.promise},function(err){throw{type:-1,data:err}})}});